  "/modules/uix/display/button.js": {
    "content": "export default ({ T }) => ({\n\ttag: \"uix-button\",\n\tproperties: { variant: T.string(\"default\") },\n\textends: \"uix-link\",\n\tstyle: true,\n\tsetVariant() {\n\t\tconst { variant } = this;\n\t\tthis.style.setProperty(\n\t\t\t\"--_variant-color-50\",\n\t\t\t`var(--colors-${variant}-50)`,\n\t\t);\n\t\tthis.style.setProperty(\n\t\t\t\"--_variant-color-300\",\n\t\t\t`var(--colors-${variant}-300)`,\n\t\t);\n\t\tthis.style.setProperty(\n\t\t\t\"--_variant-color-400\",\n\t\t\t`var(--colors-${variant}-400)`,\n\t\t);\n\t\tthis.style.setProperty(\n\t\t\t\"--_variant-color-500\",\n\t\t\t`var(--colors-${variant}-500)`,\n\t\t);\n\t\tthis.style.setProperty(\n\t\t\t\"--_variant-color-600\",\n\t\t\t`var(--colors-${variant}-600)`,\n\t\t);\n\t\tthis.style.setProperty(\n\t\t\t\"--_variant-color-700\",\n\t\t\t`var(--colors-${variant}-700)`,\n\t\t);\n\t\tthis.style.setProperty(\n\t\t\t\"--_variant-color-800\",\n\t\t\t`var(--colors-${variant}-800)`,\n\t\t);\n\t},\n\tconnected() {\n\t\tthis.setVariant();\n\t},\n\twillUpdate(changedProps) {\n\t\tif (Object.keys(changedProps).includes(\"variant\")) {\n\t\t\tthis.setVariant();\n\t\t}\n\t},\n});\n",
    "mimeType": "application/javascript",
    "skipSW": false
  },
